const initialState = {
    todos: [],
    currentTodoTitle: ""
};

export const todos = (state = initialState, action) => {
    switch (action.type) {
        case "CREATE_TODO":
            const newTodo = { 
                id: action.payload.id, 
                title: action.payload.title, 
                completed: false 
            }
            const newTodos = [
                ...state.todos,
                newTodo
            ];
            return { ...state, tofod: newTodos };
        case "UPDATE_TODO_TITLE":
            return { ...state, currentTodoTitle: action.payload.New}
        default:
            return state;
    }
};

export default todos;
